package com.geeks.nurkyz_chomoeva_hw3_4;

import android.os.Bundle;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.recyclerview.widget.RecyclerView;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;

import com.geeks.nurkyz_chomoeva_hw3_4.databinding.FragmentRVBinding;

import java.util.ArrayList;


public class RVFragment extends Fragment implements OnItemClick{

    FragmentRVBinding binding;

    PizzaAdapter adapter;
    private ArrayList<PizzaModel> pizzaModel = new ArrayList<>();


    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        // Inflate the layout for this fragment
        binding = FragmentRVBinding.inflate(getLayoutInflater());
        return binding.getRoot();
    }


    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
        loadDate();
        adapter = new PizzaAdapter(pizzaModel, this);
        binding.rvView.setAdapter(adapter);
    }

    private void loadDate() {
        pizzaModel.add(new PizzaModel("Peperoni", "data:image/jpeg;base64,","Peporoni good pizza", "5€"));
        pizzaModel.add(new PizzaModel("Margarita", "data:image/jpeg;base64,","Margarita bad pizza", "4€"));
        pizzaModel.add(new PizzaModel("Combo", "data:image/jpeg;base64,","2 pizzas + drink", "12€"));
        pizzaModel.add(new PizzaModel("Chilli", "data:image/jpeg;base64,","Chilli very spisy pizza", "5€"));
        pizzaModel.add(new PizzaModel("Tiramisu", "data:image/jpeg;base64,","Tiramisu is sweeet desert", "3€"));
        pizzaModel.add(new PizzaModel("Lemonade", "data:image/jpeg;base64,","Quenches your thirst", "2€"));
        pizzaModel.add(new PizzaModel("Ice-cream", "data:image/jpeg;base64,","Just perfect", "1€"));
    }

    @Override
    public void onClick(int position) {
        PizzaModel pizza = pizzaModel.get(position);
        Bundle bundle = new Bundle();
        bundle.putSerializable("pizza", pizza);
        DetailFragment fragment = new DetailFragment();
        fragment.setArguments(bundle);
        requireActivity().getSupportFragmentManager().beginTransaction().replace(R.id.fragment_container_view,fragment).addToBackStack(null).commit();
    }
}